        .clk_i,
        .rst_ni,
        .clr_i    (1'b0),
        .wvalid_i (kmac_rom_vld),
        .wready_o (kmac_rom_rdy),
        .wdata_i  ({kmac_rom_last, checker_rom_rdata}),
        .rvalid_o (kmac_rom_vld_outer),
        .rready_i (kmac_rom_rdy_outer),
        .rdata_o  ({kmac_rom_last_outer, checker_rom_rdata_outer}),
        .full_o   (),
        .depth_o  (),
        .err_o    ()
      );
